(1983, ' 150, 1-2. 3-4. 5', '<div>Chvá¾te Pána v jeho svätyni, *<br />\nchvá¾te ho na jeho vznešenej oblohe.<br />\nChvá¾te ho za jeho èiny mohutné, *<br />\nchvá¾te ho za jeho nesmiernu velebnos.	<var>R.</var><br />\nChvá¾te ho zvukom po¾nice, *<br />\nchvá¾te ho harfou a citarou.<br />\nChvá¾te ho bubnom a tancom, *<br />\nchvá¾te ho lırou a flautou.	<var>R.</var><br />\nChvá¾te ho ¾ubozvuènımi cimbalmi, †<br />\nchvá¾te ho jasavımi cimbalmi; *<br />\nvšetko, èo dıcha, nech chváli Pána.	<var>R.</var></div>'),
(1984, ' 16, 1-2a+5. 7-8. 11', '<div>Ochráò ma, Boe, k tebe sa utiekam. *<br />\nHovorím Pánovi: "Ty si môj Pán."<br />\nTy, Pane, si môj podiel na dedièstve a na kalichu, *<br />\nv tvojich rukách je môj osud.	<var>R.</var><br />\nVelebím Pána, èo ma múdrosou obdaril; *<br />\nv noci ma k tomu moje srdce vyzıva.<br />\nPána mám vdy pred oèami; *<br />\na pretoe je po mojej pravici, nezakolíšem sa.	<var>R.</var><br />\nUkáeš mi cestu ivota. †<br />\nU teba je plnos radosti, *<br />\npo tvojej pravici veèná slas.	<var>R.</var></div>');
