(2312, ' 98, 1. 2-3b. 3c-4', '<div>Spievajte Pánovi pieseò novú, *<br />\nlebo vykonal veci zázraèné.<br />\nVíazstvo je dielom jeho pravice *<br />\na jeho svätého ramena.	<var>R.</var><br />\nPán oznámil svoju spásu, *<br />\npred oèami pohanov vyjavil svoju spravodlivos.<br />\nRozpamätal sa na svoju dobrotu *<br />\na na svoju vernos voèi Izraelovmu domu.	<var>R.</var><br />\nUzreli všetky konèiny zeme *<br />\nspásu nášho Boha.<br />\nNa chválu Boiu jasaj, celá zem; *<br />\nplesajte, radujte sa a hrajte.	<var>R.</var></div>'),
(2313, ' 98, 1. 2-3b. 3c-4. 5-6', '<div>Spievajte Pánovi pieseò novú, *<br />\nlebo vykonal veci zázraèné.<br />\nVíazstvo je dielom jeho pravice *<br />\na jeho svätého ramena.	<var>R.</var><br />\nPán oznámil svoju spásu, *<br />\npred oèami pohanov vyjavil svoju spravodlivos.<br />\nRozpamätal sa na svoju dobrotu *<br />\na na svoju vernos voèi Izraelovmu domu.	<var>R.</var><br />\nUzreli všetky konèiny zeme *<br />\nspásu nášho Boha.<br />\nNa chválu Boiu jasaj, celá zem; *<br />\nplesajte, radujte sa a hrajte.	<var>R.</var><br />\nHrajte Pánovi na citare, *<br />\nna citare a na harfe zunivej.<br />\nZa hlaholu trúb a po¾níc *<br />\njasajte pred tvárou Krá¾a a Pána.	<var>R.</var></div>'),
